import emailjs from "emailjs-com";
import React, { useState } from "react";
import "./contactForm.css";
import { Link } from "react-router-dom";

function ContactUs() {
  const form = document.getElementById("form");
  const username = document.getElementById("name");
  const email = document.getElementById("email");
  const password = document.getElementById("password");
  const password2 = document.getElementById("password2");

  form.addEventListener("submit", (e) => {
    e.preventDefault();

    checkInputs();
  });

  function checkInputs() {
    // trim to remove the whitespaces
    const usernameValue = username.value.trim();
    const emailValue = email.value.trim();
    const passwordValue = password.value.trim();
    const password2Value = password2.value.trim();

    if (usernameValue === "") {
      setErrorFor(username, "Username cannot be blank");
    } else {
      setSuccessFor(username);
    }

    if (emailValue === "") {
      setErrorFor(email, "Email cannot be blank");
    } else if (!isEmail(emailValue)) {
      setErrorFor(email, "Not a valid email");
    } else {
      setSuccessFor(email);
    }

    if (passwordValue === "") {
      setErrorFor(password, "Password cannot be blank");
    } else {
      setSuccessFor(password);
    }

    if (password2Value === "") {
      setErrorFor(password2, "Password2 cannot be blank");
    } else if (passwordValue !== password2Value) {
      setErrorFor(password2, "Passwords does not match");
    } else {
      setSuccessFor(password2);
    }
  }

  function setErrorFor(input, message) {
    const formControl = input.parentElement;
    const small = formControl.querySelector("small");
    formControl.className = "form-control error";
    small.innerText = message;
  }

  function setSuccessFor(input) {
    const formControl = input.parentElement;
    formControl.className = "form-control success";
  }

  function isEmail(email) {
    return /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
      email
    );
  }
  function sendEmail(e) {
    console.log("sends mail");
    e.preventDefault();
    emailjs
      .sendForm(
        "service_gk688ip",
        "template_t8bvc8d",
        e.target,
        "user_LLQ9jJYKS5bbiRNCrgoJ7"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
    alert("בקשתך התקבלה במערכת, נציגנו יחזרו אליך בהקדם");
  }
  return (
    <div className="wrapper">
      <div className="title">
        <h1>מלא את פרטיך ונחזור אליך בהקדם</h1>
      </div>
      <div className="contact-form">
        <div className="input-fields">
          <form onSubmit={sendEmail}>
            <input
              type="text"
              className="input"
              placeholder="שם"
              name="message"
              id="message"
            />
            <input
              type="text"
              name="email"
              id="email"
              className="input"
              placeholder="כתובת אימייל"
            />
            <input
              type="text"
              className="input"
              placeholder="מספר פלאפון לחזרה"
              name="phone"
              id="phone"
            />
            <input
              type="text"
              className="input"
              placeholder="תיאור הבקשה"
              name="message"
              id="message"
            />

            <input
              type="submit"
              value="שלח פנייה"
              className="btnContactForm"
            ></input>
          </form>
        </div>
      </div>
      <h2 className="phone-num">אנו זמינים בטלפון: 052-708-8919</h2>
    </div>
  );
}

export default ContactUs;
