import emailjs from "emailjs-com";
import React, { useState } from "react";
import "./contactForm.css";
import { Link } from "react-router-dom";
import { Button } from "./Button";

function ContactUs() {
	function sendEmail(e) {
		// console.log("sends mail");
		// e.preventDefault();
		// emailjs
		// 	.sendForm(
		// 		"service_gk688ip",
		// 		"template_t8bvc8d",
		// 		e.target,
		// 		"user_LLQ9jJYKS5bbiRNCrgoJ7"
		// 	)
		// 	.then(
		// 		(result) => {
		// 			console.log(result.text);
		// 		},
		// 		(error) => {
		// 			console.log(error.text);
		// 		}
		// 	);
		// e.target.reset();
		// alert("בקשתך התקבלה במערכת, נציגנו יחזרו אליך בהקדם");
		// setDone(true);
	}

	function validateName() {
		var name = document.getElementById("name").value;
		if (name.length == 0) {
			alert("Name can't be blank");
			return false;
		}
		if (!name.match(/^[a-zA-Z]{3,}(?: [a-zA-Z]+){0,2}$/)) {
			alert("Please enter your correct name"); //Validation Message
			return false;
		}
		return true;
	}

	function validatePhone() {
		var phone = document.getElementById("phone").value;
		if (phone.length == 0) {
			alert("Phone number can't be blank"); //Validation Message
			return false;
		}

		if (!phone.match(/^[0]?[789]\d{9}$/)) {
			alert("Please enter a correct phone number"); //Validation Message
			return false;
		}

		return true;
	}

	function validateEmail() {
		var email = document.getElementById("email").value;
		if (email.length == 0) {
			alert("Email can't be blank"); //Validation Message
			return false;
		}

		if (!email.match(/^[A-Za-z\._\-[0-9]*[@][A-Za-z]*[\.][a-z]{2,4}$/)) {
			alert("Please enter a correct email address"); //Validation Message
			return false;
		}

		return true;
	}

	function validateForm() {
		if (!validateName() || !validatePhone() || !validateEmail()) {
			alert("Form not submitted"); //Validation Message
			return false;
		} else {
			submitted = true;
			return true;
		}
	}
	const [done, setDone] = useState(false);

	return (
		<>
			<video src="/videos/Typing_dark.mp4" autoPlay loop muted />
			<div className={done ? "done" : "hide"}>
				{/* <div className="hide"> */}
				<div className="ty">
					<h1>
						תודה שבחרת <span className="stringel">סטרינגל</span>
					</h1>
				</div>
				<br />
				<div className="contact-btn">
					<div className="to-home">
						<Button
							className="btns"
							buttonStyle="btn--outline"
							to="/"
							onClick={console.log("Redirect to Home Page")}
						>
							<i class="fas fa-home"></i> חזרה לדף הבית
						</Button>
					</div>
					<div className="to-prog">
						<Button
							className="btns"
							buttonStyle="btn--outline"
							to="/Resume"
							onClick={console.log("Redirect to Home Page")}
						>
							<i class="fas fa-laptop-code margin"></i>
							לעמוד המתכנתים שלנו
						</Button>
					</div>
				</div>
			</div>
			<div className={done ? "hide" : "wrapper"}>
				{/* <div className="wrapper"> */}
				<div className="title">
					<h1>מלא את פרטיך ונחזור אליך בהקדם</h1>
				</div>
				<div className="contact-form">
					<div className="input-fields">
						<script type="text/javascript">var submitted=false;</script>

						<iframe
							name="hidden_iframe"
							id="hidden_iframe"
							style="display:none;"
							onload="if(submitted) {window.location='http://zerobudget.club/solutions/thank-you.html';}"
						></iframe>
						<form
							target="hidden_iframe"
							onsubmit="return validateForm();"
							id="form"
							onSubmit={sendEmail}
							action="https://docs.google.com/forms/u/0/d/e/1FAIpQLSczB2hIJR3pHBntxdXTQMVCvAGmS3YQe33vhB2tcRBjBe9g0Q/formResponse"
							method="post"
						>
							<input
								dir="rtl"
								required
								type="text"
								className="input"
								placeholder="שם"
								name="entry.106556848"
								id="name"
							/>
							<input
								pattern="[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?"
								required
								type="text"
								name="entry.125606592"
								id="email"
								className="input"
								placeholder="כתובת אימייל"
							/>
							<input
								required
								type="text"
								className="input"
								placeholder="מספר פלאפון לחזרה"
								name="entry.1427034964"
								id="phone"
							/>
							<input
								type="text"
								className="input"
								placeholder="?מה אתה מעוניין שנפתח עבורך"
								name="entry.134344889"
								id="message"
							/>
							<div className="btnContactForm">
								<input
									type="submit"
									value="שלח פנייה"
									className="submit-btn"
								></input>
							</div>
						</form>
					</div>
				</div>
				<h2 className="phone-num">:אנו זמינים בטלפון </h2>
				<h2 className="phone-num">052-708-8919</h2>
			</div>
		</>
	);
}

export default ContactUs;
